INSERT INTO `sys_book` VALUES ('1965', '2065', 'I247.54/L762c2', '大雪无痕', '长篇小说-中国-当代', '本小说是著名作家陆天明的力作。在省市领导为衣锦还乡的军区丁司令员举办的招待会开始前,松江市东钢股票案重要知情人、市委张秘书被枪杀在宾馆的阁楼上。随着枪响,故事情节慢慢展开,方雨林凭着自己敏锐的侦察能力,查到案件并不是那么简单。小说入木三分地剖析了权欲私欲膨胀后,人性畸变的痛苦而又丑陋的历程,', 2017, '陆天明', '978-7-5354-6251-0', '', 'I247.54', '', '长江文艺出版社', '2019-01-04 17:58:53', '', NULL, '1240278', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('1966', '2066', 'null', '大雨将至', '短篇小说-小说集-中国', '本书是一本短篇小说集，黄杰的小说具有较高的辨识度，语言轻巧灵动，具有诗化的性质，叙事松紧有致，有突破现实的企图。无论从结构、意象、叙述、语言等方面，都已达到一定的成熟度，最主要的是，小说中透露出一种天然的才华。而这本书里面精选了他的十篇短篇小说。', 2018, '黄杰', '978-7-5596-1300-4', '', 'I247.7', '', '北京联合出版公司', '2019-01-04 17:58:53', '', NULL, '1252199', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
