INSERT INTO `sys_book` VALUES ('1970', '2070', 'null', '达瑟与达戈', '长篇小说-中国', '本书讲述了达瑟与达戈，两个年轻人，一对好朋友，却有着别样的人生。一个是蜗居树屋、不断思索的愚人，一个是为爱情甘做傻瓜的精明人。他们像冰与火，利箭与猎枪，大树与堡垒，映射出复杂丰富的人性特质。', 2018, '阿来', '978-7-5339-5093-4', '', 'I247.57', '', '浙江文艺出版社', '2019-01-04 17:58:53', '', NULL, '1256794', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
