INSERT INTO `sys_book` VALUES ('1987', '2087', 'null', '地下巴黎', '长篇小说－法国－现代', '《地下巴黎》共分为21个章节,每个章节都是1个世纪,分别以巴黎的地铁站来命名。洛朗·多伊奇以巴黎地铁站为切入口,用生动有趣的语言介绍了巴黎的历史,及至整个法国的历史。为了增加阅读的趣味性,作者在书中不时穿插一些鲜为人知的轶事,比如,巴黎的起源并不是法兰西岛而是楠泰尔;', 2018, '多伊奇,施珂', '978-7-5327-7503-3', '', 'I565.45', '', '上海译文出版社', '2019-01-04 17:58:54', '', NULL, '1255686', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
