INSERT INTO `sys_book` VALUES ('1989', '2089', 'I247.57/M289j', '地主的女儿', '长篇小说-中国-当代', '本书是一部中国当代小说，讲述了女知青蒋苦菊从小就知道，她的家庭和别人不一样，她是地主的女儿。为了能加入少先队，她的学习成绩永远第一；为了公社的小高炉能练出铁水，她争做捡废铁小状元；为了替旧社会剥削了穷人的爷爷和姥爷赎罪，她甘愿忍受雇农的儿子刘解放的打骂和侮辱。她所有的努力都是为了一个目标：一定要成为共产主义的接班人。', 2014, '孟翔勇', '978-7-307-11990-1', '', 'I247.57', '', '武汉大学出版社', '2019-01-04 17:58:54', '', NULL, '1260351', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('199', '299', 'null', '计算机博弈平台构建研究', '计算机网络-软件工具', '本书围绕计算机网络博弈平台的构建,阐述了其构建目的、意义,也对计算机网络博弈平台模型进行了分类,就计算机网络博弈平台构建过程中的关键问题给出了解决方案。同时,以一个点格棋计算机网络博弈平台软件的主要代码和使用说明为例,进行了构建过程的阐述。', 2017, '张利群', '978-7-5114-4653-4', '', 'TP393', '', '中国石化出版社', '2019-01-04 17:57:52', '', NULL, '1244287', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
