INSERT INTO `sys_book` VALUES ('1995', '2095', 'null', '电光', '长篇小说－法国－现代', '《电光》以真实人物为原型，是艾什诺兹三部“人物小说”中的一种。这样的文体是艾什诺兹寻找新的空间的尝试，在《电光》之后，他又重新回到了虚构小说的创作上。《电光》的主人公是尼古拉·特斯拉。小说以引人入胜的小说笔法，在较短的篇章中，展现了特斯拉神秘而变化的一生。', 2017, '', '978-7-5404-8372-2', '', 'I565.45', '', '湖南文艺出版社', '2019-01-04 17:58:54', '', NULL, '1261409', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
