INSERT INTO `sys_book` VALUES ('20', '120', 'TP311.13/W217a', '大数据时代:数据保护与流动规则', '计算机安全', '本书在对大数据产业、应用、技术做概貌介绍的基础上，重点阐述国内外有关大数据发展的重要规则，包括在数据保护、数据交易、数据跨境流动、数据开放等方面的国家战略、立法、监管政策，既包括对规则的全面系统介绍，也包括国际上围绕相关规则的新发展动态。', 2017, '王融', '978-7-115-43919-2', '', 'TP311.13', '', '人民邮电出版社', '2019-01-04 17:57:45', '', NULL, '1204481', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
