INSERT INTO `sys_book` VALUES ('2021', '2121', 'I247.57/L535j', '房思琪的初恋乐园', '长篇小说-中国-当代', '这是一部惊人而特别的小说,小说作者既具有高度敏锐的感受力、又是一个近距离目击者,使这整件事像一个“幸存的标本”那样地被保留下来。整本书反覆地、用极度贴近被侵害者的视角,直直逼视那种“别人夺去你某个珍贵之物”的痛苦──且掠夺之人是以此为乐。', 2018, '林奕含', '978-7-5596-1463-6', '', 'I247.57', '', '北京联合出版公司', '2019-01-04 17:58:55', '', NULL, '1252934', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
