INSERT INTO `sys_book` VALUES ('2049', '2149', 'null', '古代小说与梦', '古典小说-小说研究-中国', '文学创作的对象主要是丰富多彩的现实生活与人们深入细微的内心世界,而对梦的描写也是其中非常重要的一部分。本书以中国古代小说中的梦类故事或情节为研究对象,运用的方法既立足本国的梦理论,又引入弗洛伊德、荣格等释梦大师的相关理论,尝试以中西结合的视野对这一传统题材领域进行一次深入浅出、生动有趣的解读。本书将打破时空的界限,', 2018, '周彩虹', '978-7-5668-2235-2', '', 'I207.41', '', '暨南大学出版社', '2019-01-04 17:58:55', '', NULL, '1255425', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('205', '305', 'null', '计算机导论', '电子计算机-高等学校-教材', '本书将计算机各专业方向的理论基础和基础实践操作结合在一起,每章包括基础知识、实验项目、思考探索三部分。基础知识部分讲解该方向的基础理论和原理。实验项目则是该方向的基础操作实验和综合项目实验。思考探索则提出该方向的扩展问题,引导学生思考研究。本书涉及的内容有计算机硬件、操作系统、应用软件、网络、数据库、多媒体等。', 2017, '刘均', '978-7-302-48044-0', '', 'TP3', '', '清华大学出版社', '2019-01-04 17:57:52', '', NULL, '1237956', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
