INSERT INTO `sys_book` VALUES ('2056', '2156', 'null', '怪才的荒诞与忧伤', '长篇小说－美国－现代', '这部回忆录记录了作者二十一岁时在父母双双罹患癌症去世后的几个月里抚养幼弟的经历。艾格斯的父母在他二十一岁那年，于短短三十二天内相继过世。面对突如其来的家变，他不仅成了孤儿，还得扛起抚育年幼弟弟的重责大任。为了生存，他苦中作乐，把自己不寻常的人生遭遇化作一出插科打诨的荒诞剧。在笑闹中，流露出一丝淡淡的哀愁与一股浓�', 2017, '', '978-7-02-012170-0', '', 'I712.45', '', '人民文学出版社', '2019-01-04 17:58:56', '', NULL, '1242493', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
