INSERT INTO `sys_book` VALUES ('2061', '2161', 'I512.45/J181', '鬼玩偶', '长篇小说-俄罗斯-现代', '创作于1911年的小说《鬼玩偶》,以俄国1905年间发生的真实事件为故事背景,描写主要人物尤里和米哈伊尔等人在俄国革命前的日常生活和境遇,还原俄国社会变革时期的历史面貌,展现不同阶层的人们对待俄国20世纪初历史事件的不同态度和看法。作品不仅仅是对俄罗斯历史发展进程的一个描摹,而且还将吉皮乌斯本人的哲学、审美和宗教观点融合在一起,', 2017, '吉皮乌斯,赵艳秋', '978-7-220-10305-6', '', 'I512.45', '', '四川人民出版社', '2019-01-04 17:58:56', '', NULL, '1234745', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2062', '2162', 'null', '海明威中短篇小说选', '英语－汉语－对照读物；中篇小说－小说集－美国－现代；短篇小说－小说集－美国－现代', '本书收集了海明威的《老人与海》《乞力马扎罗的雪》等中短篇小说代表作，使读者通过本书全面了解海明威的作品深刻思想内涵。《老人与海》讲述的是围绕一位老年古巴渔夫，与一条巨大的马林鱼在离岸很远的湾流中搏斗而展开的故事。《乞力马扎罗的雪》是对一个将死之人面对死亡时的精彩描述，后被改编成电影。全书采取英汉对照的形式，对于�', 2017, '', '978-7-5563-0372-4', '', 'H319.4：I', '', '天津社会科学院出版社', '2019-01-04 17:58:56', '', NULL, '1246030', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
