INSERT INTO `sys_book` VALUES ('2064', '2164', 'null', '海上英雄·海上英雄续', '侠义小说－小说集－中国－现代', '顾明道武侠创作颇多，多半以爱国或民族思想为寄托，因此很受到欢迎。其武侠小说作品可与向恺然、赵焕亭并论，并誉其为“武侠则有声有色，写社会则入情入理，记事则惟妙惟肖，言情则可歌可泣。”他一生创作武侠作品共计十八部，代表作《荒江女侠》曾被改编成戏剧又拍成电影。他在书中所用的单一观点(主观笔法', 2018, '', '978-7-5034-9920-3', '', 'I246.5', '', '中国文史出版社', '2019-01-04 17:58:56', '', NULL, '1261551', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
