INSERT INTO `sys_book` VALUES ('2066', '2166', 'null', '还乡', '长篇小说-中国', '本书描写渭河之北一个秉承耕读传统的家族，伴随百年间社会风云的变幻，五代人与守望的古槐一起经历了人生与命运的兴衰枯荣。年过花甲者在还乡的情境中，回叙个人的心灵史和过往事物的景观，亦呈现出当下乡土的精神处境。', 2017, '和谷', '978-7-5063-9642-4', '', 'I247.57', '', '作家出版社', '2019-01-04 17:58:56', '', NULL, '1235833', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
