INSERT INTO `sys_book` VALUES ('2069', '2169', 'null', '寒鸦行动', '长篇小说-英国', '本书讲述了：二战期间，英国首相丘吉尔秘密设立了一个全部为女性的情报部门，代号“寒鸦”。这群身份各异的女性：散漫的贵族、爱幻想的小女生、脾气暴躁的吉卜赛杀人犯、擅长撬保险柜的小偷、异装癖。她们临时集结，勇敢地展抗纳粹的行动。然而，她们被出卖了。一张天罗地网正等待着“寒鸦”们。', 2017, '福莱特,于大卫', '978-7-5594-1078-8', '', 'I561.45', '', '江苏凤凰文艺出版社', '2019-01-04 17:58:56', '', NULL, '1236128', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
