INSERT INTO `sys_book` VALUES ('209', '309', 'null', '计算机犯罪与电子取证研究', '计算机犯罪-证据', '本书借鉴犯罪学、刑法学、社会学及历史学等学科的理论和方法，探讨计算机犯罪的基本概念、发展和趋势，以及世界主要发达国家和我国计算机犯罪的状况及其社会危害；还从不同角度系统考察了电子证据的概念和取证，分析电子证据的类型、特点和原因，对比不同国家对电子取证的立法和处理方法，以及我国目前电子证据取证的现状，和未来发展方向。', 2018, '蒋平', '978-7-5201-1739-5', '', 'D918', '', '社会科学文献出版社', '2019-01-04 17:57:52', '', NULL, '1252138', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
