INSERT INTO `sys_book` VALUES ('2139', '2239', 'null', '记忆裂痕', '科学幻想小说－小说集－美国－现代', '该书系世界科幻大师丛书中的一本。本系列的第一册小说都是作者于1951至1952年期间九个月内完成的。此时迪克刚刚二十出头，在科幻写作界初露锋芒。展示了作者幽默、怪异又脱离寻常路线的写作风格。作品内容涉及星际探险、外星文明、时空旅行、核战的末日、智能机器人、遗传工程等。他的多部作品被改编成电影。', 2017, '', '978-7-5364-8726-0', '', 'I712.45', '', '四川科学技术出版社', '2019-01-04 17:58:58', '', NULL, '1242142', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
