INSERT INTO `sys_book` VALUES ('2140', '2240', 'null', '饥饿的艺术家', '短篇小说－小说集－世界', '《饥饿艺术家》描述了一个“痴迷”饥饿艺术的表演者从其风靡全城的荣光到被人厌弃的落漠，始终不被真正理解而孤寂痛苦直至无声死去的过程。小说着力刻划了饥饿艺术家对自己的事业——饥饿艺术的执著追求、至死不渝的坚定信念及渴望被真正理解的强烈愿望，此外还突出叙写了不解真意的观众对这种艺术的麻木和诋毁。', 2013, '', '978-7-5399-9904-3', '', 'I14', '', '江苏凤凰文艺出版社', '2019-01-04 17:58:58', '', NULL, '1242074', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2141', '2241', 'null', '假面山庄', '长篇小说－日本－现代', '八个人聚到一栋别墅，来怀念不久前去世的朋美，她在婚礼前一周出车祸去世。八人中有朋美的父母、未婚夫、表妹、挚友等。深夜，两个男人持枪闯入，挟持了众人。人质们想尽办法自救，却总因为有人暗中破坏而失败。第三天早上，朋美的表妹被发现遭人杀害，日记本也被人撕去一页，而那一页的日期正是朋美遭遇车祸的那一天。两件看似毫不相干�', 2018, '', '978-7-5302-1755-9', '', 'I313.45', '', '北京十月文艺出版社', '2019-01-04 17:58:58', '', NULL, '1250145', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
