INSERT INTO `sys_book` VALUES ('2152', '2252', 'I247.57/T223b', '脚注', '长篇小说-中国-当代', '本书是当代香港青年作家唐睿的长篇小说,获第十届香港中文文学双年奖。小说讲述火灾幸存少年黎军重回已清拆的安置区,引动恍恍惚惚的记忆与虚虚实实的叙事。这个安置区可说是香港的某个侧面,或者说是平民化香港的缩影,里面住着内地移民、印尼华侨、印尼人,', 2017, '唐睿', '978-7-5360-8393-6', '', 'I247.57', '', '花城出版社', '2019-01-04 17:58:59', '', NULL, '1222556', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
