INSERT INTO `sys_book` VALUES ('2170', '2270', 'null', '静人日记', '日记体小说-日本', '本书以日记体的形式再现了静人四处流浪、悼念不知名逝者的精神之旅。幼年时，坂筑静人目睹一只鹎鸟从树上坠落死亡；八岁时，爷爷始终对战争空袭死难的学生抱有无法释怀的歉疚，无端溺死在海洋中；成年后，他的医生好友由于连续工作三十八小时而过劳死。静人悲痛于死亡带来的虚妄感，开始了流浪之旅，哀悼一切他能找到的逝者。', 2017, '天童荒太,匡匡', '978-7-02-012904-1', '', 'I313.45', '', '人民文学出版社', '2019-01-04 17:58:59', '', NULL, '1236140', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
