INSERT INTO `sys_book` VALUES ('2188', '2288', 'I313.45/D378ap', '空中杀人现场', '长篇小说-日本-现代', '本书讲述新日航的华丽双人组，通称A子和B子。容貌与性格完全背道而驰的两个人，不知为何确实最麻吉的搭档。秀外慧中、智慧绝伦的A子与啥都两光，就是记忆力超强的B子，两人在云端上联手解决了各式各样的悬疑事件。', 2013, '东野圭吾,杨婉蘅', '978-7-5442-5721-3', '', 'I313.45', '', '南海出版公司', '2019-01-04 17:59:00', '', NULL, '1251191', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2189', '2289', 'null', '叩问小说', '小说研究', '本书以问题学哲学为指导，重新审视小说这种最庞杂的文学体裁，在检视19世纪以来主要小说理论家詹姆斯、卢卡奇、巴赫金、奥埃巴赫、热奈特和昆德拉等人的小说理论中，在逐渐批评卢卡奇和巴赫金所设置的目的化的历史言语、奥埃巴赫以《圣经》视野为基础的模仿理论、远离小说活动方式并可以概括为计算时间能力的叙述学以及本质主义的小说观等', 2017, '让贝西埃,史忠义', '978-7-5130-4913-9', '', 'I106.4', '', '知识产权出版社', '2019-01-04 17:59:00', '', NULL, '1240651', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('219', '319', 'R918/Y2792', '计算机辅助药物设计导论', '药物-计算机辅助设计-高等学校-教材', '为了适应不同学科、不同层次的读者需要,本书深入浅出地介绍了计算机辅助药物设计的入门知识,包括:药物作用的基本理论,药物设计的基本概念与方法,计算机辅助药物设计的化学信息学和生物信息学系统、理论计算基础、计算机辅助药物设计的主要策略方法和技术;重点讲解计算机辅助药物设计的意义、作用和基本研究方法,', 2017, '付伟,叶德泳', '978-7-122-29565-1', '', 'R918-43', '', '化学工业出版社', '2019-01-04 17:57:52', '', NULL, '1228984', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2190', '2290', 'null', '叩响天堂之门', '长篇小说-中国', '坐了八个小时的火车，又转乘了三个多小时的大巴车，阿满终于在大年二十九的傍晚回到了家。刚吃过晚饭，阿满把带回家的大大小小几个行李箱打开，蹲在地板上面把给每一个人带的礼物一一分发完毕后，没想到大哥阿文冷冷的一句话把阿满给说愣了……', 2017, '蔡劲松', '978-7-5063-9663-9', '', 'I247.57', '', '作家出版社', '2019-01-04 17:59:00', '', NULL, '1247233', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
