INSERT INTO `sys_book` VALUES ('2192', '2292', 'null', '苦行记', '自传体小说-美国', '《苦行记》是奠定马克·吐温幽默大师地位的早期成名之作。该书记述了作者到美国西部的找矿经过,把艰苦的淘金生活写得生气勃勃,其中一些夸张、滑稽的故事充分反映了作者作为幽默大师的才华和语言风格。作者以独特的视角和手法描述了19世纪中叶美国社会的众生百态:发财与挥霍,追求与冒险,野心与欲望,强力与巧智,', 2017, '马克·吐温,刘文哲', '978-7-5407-8150-7', '', 'I712.44', '', '漓江出版社', '2019-01-04 17:59:00', '', NULL, '1232199', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
