INSERT INTO `sys_book` VALUES ('2200', '2300', 'null', '浪漫的事', '短篇小说-小说集-中国', '本书为范小青最新短篇小说结集。作品延续了作者一贯委婉细腻、细致生动的风格与特色：小巷间小人物琐事。大量地方性语言的运用，处处流露着苏州人小家碧玉的姿态。', 2017, '范小青', '978-7-5354-9803-8', '', 'I247.7', '', '长江文艺出版社', '2019-01-04 17:59:00', '', NULL, '1257859', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2201', '2301', 'I247.57/J369c3', '狼图腾', '长篇小说-中国-当代', '本书是一部描写草原狼和游牧文化的长篇小说。上个世纪六七十年代，一批北京知青来到中国最遥远、最原始的内蒙古草原，在那个不管对人类还是对野兽来说都是难以生存的地方做了十几年的牧羊人、牧马人。知青陈阵和杨克对草原文化、对草原狼的知识和传奇产生了浓厚的兴趣，渐渐地也和牧民一样畏狼、杀狼、又敬狼，甚至亲手养大了一匹小狼。�', 2014, '姜戎', '978-7-5354-7689-0', '', 'I247.57', '', '长江文艺出版社', '2019-01-04 17:59:00', '', NULL, '1223328', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2202', '2302', 'null', '琅琊榜之风起长林', '长篇小说－中国－当代', '本书为《琅琊榜》后传。长篇小说,当年的赤焰军少帅林殊将忠耿的靖王萧景琰推上帝王之路,为万千赤焰忠魂争得朗朗青天,并以身代薪为大梁带来了清明朝局、安稳边境。大梁江山安稳时日长久,边境难免战火不断。长林军守护大梁北境,屡获军功威名赫赫。但功名太盛,朝堂中隐隐掀起猜忌防范的暗潮。琅琊阁依然置身事外淡看风云,', 2017, '海宴', '978-7-5339-3390-6', '', 'I247.5', '', '浙江文艺出版社', '2019-01-04 17:59:00', '', NULL, '1256008', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2203', '2303', 'I247.5/Z274d', '劳燕', '长篇小说-中国-当代', '本书是海外华语作家张翎的一部抗战背景的小说。故事开端于一个江南的采茶日，阿燕和刘兆虎这一对青梅竹马的恋人，在这春和景明中细数着各自的小心思。然而日军的突然空袭，留给这个美丽的茶园一个惊心的弹坑，也抽走了全书中一段闲适的有序时光。母亲的惨死，将阿燕孤零零扔在这个凶险的世界。自此后，她要面临活下去的生计，要面临爱人�', 2017, '张翎', '978-7-02-012566-1', '', 'I247.5', '', '人民文学出版社', '2019-01-04 17:59:00', '', NULL, '1222447', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
