INSERT INTO `sys_book` VALUES ('2206', '2306', 'H319.4:I712.45/H132', '老人与海', '英语-语言读物,长篇小说-美国-现代', '本书主要讲述了老渔夫桑提亚戈在海上的捕鱼经历，描写老人制服大鱼后，在返航途中又同鲨鱼进行惊险的搏斗。', 2016, '海明威,陈璐,胡玥', '978-7-5446-4251-4', '', 'H319.4:I712.45,H319.4:I', '', '上海外语教育出版社', '2019-01-04 17:59:00', '', NULL, '1256519', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2207', '2307', 'null', '了不起的盖茨比', '长篇小说', '《了不起的盖茨比》讲述了“了不起”的大亨盖茨比的悲情故事，盖茨比代表了勇敢追求理想，有着美好坚定信念的人，他执着于昨日的纯情，拼命地想重温往昔美好时光，不料，他以生命为代价换取的完美爱情，其实是破灭的金钱泡沫。', 2017, '菲茨杰拉德,吴建国', '978-7-02-012718-4', '', 'I712.45', '', '人民文学出版社', '2019-01-04 17:59:00', '', NULL, '1250973', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2208', '2308', 'null', '利图马在安第斯山', '长篇小说－秘鲁－现代', '本书为长篇小说。安第斯山深处的小镇纳克斯连续发生三起人员失踪案，哨所班长利图马和助手托马斯·加列尼奥前去调查。他们四处奔走、打听，却在恐怖分子之外，探查到另一股力量的存在，这股力量似乎更恐怖、更令人讳莫如深，案件也变得越来越扑朔迷离。为了解闷，每天晚上，加列尼奥给利图马讲述他与一个妓女的爱情故事……', 2017, '', '978-7-02-012841-9', '', 'I778.45', '', '人民文学出版社', '2019-01-04 17:59:00', '', NULL, '1242145', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
