INSERT INTO `sys_book` VALUES ('2210', '2310', 'null', '李平戏曲小说论集', '戏曲评论,小说评论-文集,文集-中国,中国', '本书收录论文38篇，均为李平教授有关戏曲小说研究的代表性论文。包括对中国戏曲形成与发展的探讨，对明代演剧状况的考察，对《金貂记》、孟姜女故事等具体曲目的演变及其影响的分析，对白朴、冯梦龙等戏曲作家的评议等几方面的成果。', 2017, '李平', '978-7-5475-1315-6', '', 'J826-53|I207.4-53', '', '中西书局', '2019-01-04 17:59:00', '', NULL, '1243309', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
