INSERT INTO `sys_book` VALUES ('2212', '2312', 'null', '离岸', '自传体小说－中国－当代', '《离岸》是一部跨文体写作的长篇自传小说。作者将现实生活中一个普通知识分子的命运与土尔扈特蒙古部落东归的传奇故事相穿插，通过对“无数个生活碎片”做“忠实地记录”，再现了一代人的成长经历、生命体验和心路历程，浸透着作者关于社会、民族、人生、文化、历史的思考，从多个角度反映了不平凡的、急遽变化的一个时代。“我的一生”�', 2017, '', '978-7-5063-9823-7', '', 'I247.5', '', '作家出版社', '2019-01-04 17:59:00', '', NULL, '1261548', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
