INSERT INTO `sys_book` VALUES ('2215', '2315', 'null', '黎明之街', '长篇小说-日本', '本书讲述了秋叶十六岁的时候，父母离异，不久母亲自杀身亡，父亲的情人又横尸家中。警察怀疑秋叶是凶手，但找不到确凿证据，于是一直对秋叶追查不休。就在诉讼时效即将到期时，秋叶遇到了家庭幸福的渡部，和他发展出一段恋情。渡部深陷与秋叶爱情的同时，却又对她的身世产生无限疑虑。于是，大家一起迎来了诉讼时效到期的这一天。', 2018, '东野圭吾,李超楠', '978-7-5442-9136-1', '', 'I313.45', '', '南海出版公司', '2019-01-04 17:59:01', '', NULL, '1257489', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
