INSERT INTO `sys_book` VALUES ('2217', '2317', 'I247.57/L393b', '梁光正的光', '长篇小说-中国-当代', '风是突然来的。勇智记得很清楚，他正用力往上提卷闸门，那闸门被轨道里的陈年老灰吸着，很难拉起。突然，他感觉胳膊上的肱二头肌鼓起的地方被什么轻扫了一下，里面的青筋一阵猛烈弹动，像一排细针轻轻扎下，又迅速拔起，点点烧灼般的疼……', 2017, '梁鸿', '978-7-02-013399-4', '', 'I247.57', '', '人民文学出版社', '2019-01-04 17:59:01', '', NULL, '1240307', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2218', '2318', 'null', '聊斋志异:会校会注会评本', '笔记小说-中国', '本书共分十二卷，是我国文学史上一部著名的短篇小说集。作者以民间流传的故事为基础，通过他的艺术加工，创造出不少优秀作品。书中除了对蒲松龄和《聊斋志异》作了评价之外，还对本书的会校、会注、会评分别作了分析批判。', 2011, '蒲松龄,张友鹤', '978-7-5325-5727-1', '', 'I214.1|I207.419', '', '上海古籍出版社', '2019-01-04 17:59:01', '', NULL, '1264783', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
