INSERT INTO `sys_book` VALUES ('2227', '2327', 'null', '猎人笔记', '长篇小说-俄罗斯', '全书以一个猎人的行猎线索，串起二十五篇自成起讫的故事，如一曲曲独立的乐章，奏响在俄罗斯广袤深沉的土地上：夜气未散的森林清晨，星空穹隆的沉默草原，空气中饱合苦艾的新鲜苦味和荞麦甘香，桦树笔直金黄，白色尖顶教堂，小屋里闪着燃烧柴火的红光，门后传出带着睡意的人声。', 2017, '屠格涅夫,耿济之', '978-7-5313-5237-2', '', 'I512.44', '', '春风文艺出版社', '2019-01-04 17:59:01', '', NULL, '1247249', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
