INSERT INTO `sys_book` VALUES ('2230', '2330', 'null', '另一个布鲁克林', '长篇小说－美国－现代', '这是一部美国现代文学长篇小说，荣获2016年纽约时报、亚马逊年度二十大好书。背景讲述的是20世纪70年代的纽约布鲁克林。男人们从战场归来，空气中弥漫着异样的躁动。有的人归来拖着残破的肢体，有的人归来带着残破的灵魂，还有些人永远地留在了战场——战争让所有人不得完整。故事便是发生在这样的气氛中。黑人女孩奥古斯特（August）的母亲是', 2017, '', '978-7-5086-7938-9', '', 'I712.45', '', '中信出版社', '2019-01-04 17:59:01', '', NULL, '1227974', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
