INSERT INTO `sys_book` VALUES ('2245', '2345', 'I210.6/L725ab', '鲁迅小说散文初刊集', '鲁迅小说-小说集,鲁迅散文-散文集', '《鲁迅小说、散文初刊集》是上海鲁迅纪念馆经过多年搜集整理，精心策划的一部鲁迅先生文献资料集成。本着考镜源流的文献学宗旨，将鲁迅先生从1918年在《新青年》上发表《呐喊》所收的《狂人日记》开始至1927年在《莽原》上发表《朝花夕拾》的《后记》为止的所有刊物图影搜集、编纂成为一册，以飨鲁迅文化的研究者和爱好者。规模虽不大，但意', 2016, '鲁迅,郑亚', '978-7-5458-1232-9', '', 'I210.6,I210.4', '', '上海书店', '2019-01-04 17:59:02', '', NULL, '1260353', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2246', '2346', 'I210.6/W459', '鲁迅与明清小说关系研究', '鲁迅,-关系-古典小说-小说研究-中国-明清时代', '本书在文学、社会学、文化学及哲学等视角下，以文本细读与阐释、实证研究、比较方法、逻辑推理等综合方法，对鲁迅与明清小说关系进行探讨，主要从鲁迅对明清小说的研究，明清小说至鲁迅小说的思想文化艺术流变及明清小说与鲁迅杂文关系三个方面入手，尤其以探讨中国古代小说向现代小说转变的本质性特点为重点。力求在文学文本批评，文学�', 2017, '吴金梅', '978-7-5203-0539-6', '', 'I210.6,I207.41', '', '中国社会科学出版社', '2019-01-04 17:59:02', '', NULL, '1223174', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
