INSERT INTO `sys_book` VALUES ('2310', '2410', 'null', '纳齐斯与戈德蒙', '长篇小说－德国－现代', '《纳齐斯与戈德蒙》是作者1930年出版的力作,叙述代表理性的禁欲主义与代表情欲感官享乐主义的一对朋友的生活经历。修道院年轻的见习修士纳齐斯才华过人,深得院长丹尼尔喜爱。新来的学生戈德蒙迷恋世俗生活,他们虽成了朋友,但戈德蒙无视纳齐斯的理性的说教,偷偷去村子里和姑娘幽会,之后又逃出修道院,四处流浪,历经欢愉与艰辛;', 2018, '黑塞,魏育青', '978-7-5327-7630-6', '', 'I516.45', '', '上海译文出版社', '2019-01-04 17:59:04', '', NULL, '1255884', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
