INSERT INTO `sys_book` VALUES ('2313', '2413', 'null', '南极', '短篇小说－小说集－爱尔兰－现代', '短篇小说集-作品集-爱尔兰-当代，《南极》是爱尔兰隆尼文学奖和洛杉矶时报图书大奖获奖作品，是爱尔兰当代最受瞩目的女作家克莱尔·吉根的第一部短篇小说集。短篇集里有十五个故事：一个婚姻幸福的女人幻想一场美妙的出轨，而当艳遇来到，却发现一切无法控制；一个失去女儿的父亲，饱受自责的煎熬，同时也承受着来自妻子的心理折磨；一个留', 2018, '', '978-7-5442-6316-0', '', 'I562.45', '', '南海出版公司', '2019-01-04 17:59:04', '', NULL, '1250664', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
