INSERT INTO `sys_book` VALUES ('2324', '2424', 'null', '潘达雷昂上尉和劳军女郎', '长篇小说－秘鲁－现代', '《潘达雷昂上尉和劳军女郎》于1973年出版并拍成影片，在西语世界乃至全球范围都产生了广泛的影响。略萨利用这个可笑而又可怕的故事，揭露了相对于世界上最古老的职业而言，号称典范的所谓军事化制度更加地虚伪。可以说，这部小说相对略萨的所有作品来说都是一种转型，显示了作者在文学里运用戏谑和幽默的才能，展示了多元而又真实的人性层�', 2009, '', '978-7-02-012846-4', '', 'I778.45', '', '人民文学出版社', '2019-01-04 17:59:04', '', NULL, '1242154', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('2325', '2425', 'I712.45/K147q', '磐石上的阴影', '长篇小说-美国-现代', '小说以17世纪法国在加拿大魁北克殖民地的居民生活为背景,讲述以药剂师奥克莱尔父女为代表的法国殖民者在新大陆上顽强生活的故事。小说以奥克莱尔家的客厅作为联系上、下两城的枢纽,通过来往于其间的各类人物的故事,讲述各自生命中难以承受的遭际和痛苦,展开一幅魁北克早期移民生活的全景图,', 2018, '凯瑟,颜红菲', '978-7-307-19892-0', '', 'I712.45', '', '武汉大学出版社', '2019-01-04 17:59:04', '', NULL, '1252511', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
