INSERT INTO `sys_book` VALUES ('237', '337', 'F239.1/C348c', '计算机审计', '计算机审计-教材', '本书系统地分析了国内外关于计算机辅助审计的研究与应用现状，结合案例重点介绍了面向数据的计算机辅助审计（电子数据审计）的关键步骤：审计数据采集、审计数据预处理、审计数据分析，并介绍了大数据环境下的电子数据审计等最新理论知识。附录中设计了6个实验模块，满足开设实验课程的需要。', 2017, '陈伟', '978-7-300-24327-6', '', 'F239.1', '', '中国人民大学出版社', '2019-01-04 17:57:53', '', NULL, '1208097', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
