INSERT INTO `sys_book` VALUES ('24', '124', 'null', '大学计算机', '电子计算机-高等学校-教材', '全书共分为9章,主要内容包括:计算机与计算思维、信息技术基础、操作系统基础、算法与数据结构、程序设计基础、多媒体技术、数据库技术基础、计算机网络和软件工程。本书以计算思维为统领和导向,结构严谨,叙述准确,实践性强。重点培养学生利用计算机处理和解决实际问题的理念、方法和综合应用能力,', 2017, '包空军,陈嫄玲', '978-7-121-32381-2', '', 'TP3', '', '电子工业出版社', '2019-01-04 17:57:46', '', NULL, '1238771', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('240', '340', 'TP391.41/P367', '计算机视觉:模型、学习和推理', '计算机视觉-研究', '本书主要讲述计算机视觉中模型、学习和推理三个方面的内容，揭示计算机视觉研究中“模型”和“算法”之间的区别，并且对每一种新的视觉算法提出新的见解。', 2017, '普林斯,苗启广,刘凯,孔韦韦,许鹏飞', '978-7-111-51682-8', '', 'TP391.41', '', '机械工业出版社', '2019-01-04 17:57:53', '', NULL, '1223460', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('241', '341', 'null', '计算机视觉检测逆问题导论', '计算机视觉－检测－研究', '本书以计算机科学技术为中心，以数学逆问题和图像处理为基础，讨论了计算机视觉检测的逆问题处理方法。本书重点介绍了用来解决数学逆问题的正则化方法及其基本原理、计算机视觉图像重建技术中的逆问题、基于前向运动视频的计算机视觉检测新思路，同时还具体描述了用于高速铁路基础设施安全检测的计算机视觉检测方法。', 2017, '', '978-7-5121-3041-8', '', 'TP391.41', '', '北京交通大学出版社', '2019-01-04 17:57:53', '', NULL, '1202336', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
