INSERT INTO `sys_book` VALUES ('243', '343', 'TP391.41/S432b', '计算机视觉增强现实应用平台开发', '计算机视觉-程序设计', '本书共分7章：第1章简要介绍AR云端制作平台的概念、结构和功能原理，第2章简要概括了主要制作流程，第3章主要介绍AR场景制作，第4章简要介绍了可使用的素材内容的规范标准，第5章重点列举了一些行业案例的制作方法，第6章简要介绍了一些AR扩展接口标准，第7章简单说明了制作平台的一些未来特性规划。', 2017, '', '978-7-111-57713-3', '', 'TP391.41', '', '机械工业出版社', '2019-01-04 17:57:53', '', NULL, '1233360', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
