INSERT INTO `sys_book` VALUES ('246', '346', 'TP303/H5122', '计算机体系结构', '计算机体系结构-研究生-教材', '本书共分为13章,内容包括:引言、计算机系统结构基础、二进制与逻辑电路、指令系统结构、静态流水线、动态流水线、多发射数据通路、转移预测、功能部件、高速缓存、存储管理、多处理器系统、实践是最好的课题。', 2017, '胡伟武', '978-7-302-48368-7', '', 'TP303-43', '', '清华大学出版社', '2019-01-04 17:57:53', '', NULL, '1237397', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('247', '347', 'TP303/B555', '计算机体系结构', '计算机体系结构-教材', '本书介绍了为理解计算机的工作原理而需要掌握的知识。为便于读者理解,本书以计算机发展历史中的典型计算机为例,阐述从第一台计算机开始,计算机技术、概念和性能的发展。全书分为五大部分,内容包括“基本体系结构的组成”、“编程模型与操作”、“内存分层结构”、“并行性与性能提升”,最后一部分附录,给出了每章练习题的答案或提示。', 2017, '布兰切特,杜波伊,陈宗斌', '978-7-302-46064-0', '', 'TP303', '', '清华大学出版社', '2019-01-04 17:57:53', '', NULL, '1223930', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
