INSERT INTO `sys_book` VALUES ('252', '352', 'TP391.411/Y497a', '计算机图形学实习教程:基于C#语言', '计算机图形学-高等学校-教材,C语言-程序设计-高等学校-教材', '本书共分8章，详细说明了如何用VisualC#语言实现计算机图形学的基本算法。内容包括：实习框架的建立，基本图形生成、二维图形填充、二维图形裁剪、图形变换、投影、消隐、曲线生成等典型算法的基本思想、实现思路和编程实现方法。同时结合算法的实现，介绍了Visual', 2017, '于子凡', '978-7-307-19655-1', '', 'TP391.411-43,TP312C-43', '', '武汉大学出版社', '2019-01-04 17:57:53', '', NULL, '1239744', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('253', '353', 'null', '计算机图形学实用技术', '计算机图形学', '本书比较全面地介绍了计算机图形学的基本知识,以及现有的成熟技术,并提供了相关编程实例。全书共9章,主要内容包括两个方面:一是计算机图形学的基本内容,涉及基本图形生成与多边形填充、字符生成、二维裁剪、二维变换、三维变换、典型曲线曲面、真实感图形基础、计算机动画技术等;二是计算机图形处理软件和实际操作技术,', 2018, '爨莹', '978-7-5606-4744-9', '', 'TP391.411', '', '西安电子科技大学出版社', '2019-01-04 17:57:53', '', NULL, '1253396', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
