INSERT INTO `sys_book` VALUES ('255', '355', 'TP393/C323e', '计算机网络', '计算机网络-高等学校-教材', '本书介绍了计算机网络和数据通信的基础知识，局域网、广域网和网络互联技术，网络互联设备，网络互联协议TCP／IP和Inter应用，网络管理与安全，以及计算机网络系统方案设计分析和投标方案。', 2017, '陈岗', '978-7-5642-2619-0', '', 'TP393-43', '', '上海财经大学出版社', '2019-01-04 17:57:53', '', NULL, '1211406', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('256', '356', 'TP393/L316e', '计算机网络', '计算机网络-高等学校-教材', '本书共由九章组成。第一章主要介绍计算机网络的组成以及性能和体系结构。包括计算机网络的基本概念、计算机网络的组成、计算机网络的类别、计算机网络的性能和计算机网络的体系结构等。第二章主要介绍与物理层相关的基本概念。', 2017, '李春杰', '978-7-03-051862-0', '', 'TP393-43', '', '科学出版社', '2019-01-04 17:57:53', '', NULL, '1206462', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('257', '357', 'TP393/W457a4', '计算机网络', '计算机网络-高等学校-教材', '本书在系统分析和总结互联网、移动互联网与物联网共性技术的基础上，重点讨论了网络的基本概念、网络体系结构、网络互联与分布式进程通信、网络应用与网络安全技术；在系统讨论网络基本工作原理的同时，注重网络应用系统与网络应用软件设计、实现方法的学习；对当前研究与应用的热点—无线网络、P2P网络与下一代网络技术进行了系统地讨论�', 2017, '吴功宜,吴英', '978-7-302-46848-6', '', 'TP393-43', '', '清华大学出版社', '2019-01-04 17:57:53', '', NULL, '1206460', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('258', '358', 'TP393/H333', '计算机网络', '计算机网络-高等学校-教材', '全书共12章,介绍了计算机网络的发展和原理体系结构、物理层、数据链路层、网络层、运输层、应用层、网络安全、因特网上的音频/视频服务、无线网络和移动网络、下一代因特网、互联网以及网络技术实验等内容。', 2017, '贺杰,潘雪峰,汪怀杰', '978-7-5643-5194-6', '', 'TP393-43', '', '西南交通大学出版社', '2019-01-04 17:57:53', '', NULL, '1202525', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('259', '359', 'TP393.08/W467a', '计算机网络安全技术研究', '计算机网络-安全技术-研究', '本书涵盖了计算机网络安全的主流技术，结构合理，重点突出。理论知识通俗易懂，以“必需、够用”为原则，突出实用性，引入行业企业技术标准。本书设计的技术都有其对应的产品支持，能够帮助学生更好地理解和掌握相关知识和技能。本书用于教师评职称，专著。', 2017, '吴朔媚,宋建卫', '978-7-5681-2220-7', '', 'TP393.08', '', '东北师范大学出版社', '2019-01-04 17:57:53', '', NULL, '1212665', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
