INSERT INTO `sys_book` VALUES ('260', '360', 'TP393.08/L321', '计算机网络安全理论与实践', '计算机网络-安全技术', '人们对信息的安全传输、安全存储、安全处理的要求越来越高。本书从网络安全角度出发，介绍计算机网络概貌、计算机网络安全，初步了解网络安全知识、介绍网络安全的相关技术、从防范的角度加深对网络安全的重要性认识；最后介绍了计算机的各种安全设计。', 2017, '李冠楠', '978-7-5677-8970-8', '', 'TP393.08', '', '吉林大学出版社', '2019-01-04 17:57:53', '', NULL, '1213214', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('261', '361', 'null', '计算机网络安全实践教程', '计算机网络-安全技术-教材', '本书参考和借鉴了广大网络信息安全人员的最新研究成果，结合了网络信息安全实践经验，系统归纳总结了网络信息安全职业人员所需知识和技能，并给出典型案例和信息安全实践指导。《计算机网络安全实践教程》内容丰富，图文并茂，讲解简明易懂、循序渐进、深入浅出。', 2017, '严小红', '978-7-5647-4685-8', '', 'TP393.08', '', '电子科技大学出版社', '2019-01-04 17:57:53', '', NULL, '1238048', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('262', '362', 'TP393/C334e', '计算机网络导论', '计算机网络-高等学校-教材', '本书共分为18章。第1章主要介绍计算机网络的基础；第2章主要介绍数据通信等；第3章主要介绍网络的组成元素；第4章主要介绍局域网络；第5章主要介绍广域网络；第6章介绍无线网络；第7章介绍IP基础等；第8章主要介绍ARP与ICMP等。', 2016, '陈明', '978-7-303-19893-1', '', 'TP393-43', '', '北京师范大学出版社', '2019-01-04 17:57:53', '', NULL, '1206461', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
