INSERT INTO `sys_book` VALUES ('265', '365', 'TP393.07/L2593', '计算机网络管理', '计算机网络管理-教材', '本书主要介绍了计算机网络管理的基本概念和应用技术，主要根据近些年网络管理发展的新趋势，对上一版进行了新的改编和补充，主要内容包括：网络管理概论、抽象语法标识ASN.1、管理信息库、简单网络管理协议、远程网络监视、SNMPC网络管理软件的应用、网络分析系统、网络管理工具和网络测试与性能评价。', 2017, '雷震甲', '978-7-5606-4439-4', '', 'TP393.07-43', '', '西安电子科技大学出版社', '2019-01-04 17:57:54', '', NULL, '1215361', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
