INSERT INTO `sys_book` VALUES ('267', '367', 'TP393/C387b', '计算机网络及应用实验指示书', '计算机网络-实验', '本书主要内容包括：基本网络操作命令；常用网络服务的配置；捕获TCP数据包；HTTP抓包分析等。', 2017, '程朋,贾庆山,曹军威', '978-7-302-46225-5', '', 'TP393', '', '清华大学出版社', '2019-01-04 17:57:54', '', NULL, '1204923', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
