INSERT INTO `sys_book` VALUES ('282', '382', 'null', '计算机网络软件编程指导书', '计算机网络-程序设计-教材', '本书共分为16章。第1章介绍了主教材的知识结构与重点,以及在实践教学方面的指导方法。第2章介绍了网络软件编程的基本概念,以及套接字编程的基本方法。第3至16章是14个独立的网络软件编程题目。每章包括编程题目的设计目的、相关知识、例题分析和练习题。', 2017, '吴英', '978-7-302-48131-7', '', 'TP393.09', '', '清华大学出版社', '2019-01-04 17:57:54', '', NULL, '1245046', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('283', '383', 'TP393/H153c', '计算机网络实训创新教程:基于CiscoIOS', '计算机网络-教材', '本书在章节内容组织上，把握“理论够用、操作为主”的原则，首先用直观的图文和表格精炼地描述相关网络知识的基本原理，然后通过综合实训案例分任务、分步骤地将前面的理论知识进行系统的应用，让复杂抽象的通信过程形象地展现在读者面前。', 2017, '韩立刚', '978-7-5170-5216-6', '', 'TP393', '', '中国水利水电出版社', '2019-01-04 17:57:54', '', NULL, '1213949', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('284', '384', 'null', '计算机网络实验教程', '计算机网络-高等学校-教材', '本书共十七个实验，内容分为网络基本原理、路由协议、网络新技术和网络综合应用四部分。包括实验入门、链路层、网络层、传输层、应用层、RIP协议、OSPF协议、BGP协议、组播、MPLS、IPv6、无线网络、无线传感器网络、网络管理、网络编程、复杂网络组建、综合组网实验。', 2017, '钱德沛,张力军', '978-7-04-047326-1', '', 'TP393', '', '高等教育出版社', '2019-01-04 17:57:54', '', NULL, '1246329', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
