INSERT INTO `sys_book` VALUES ('397', '497', 'O235/Y197', '模式识别及MATLAB实现', '模式识别-计算机辅助计算-Matlab软件,人工智能-计算机辅助计算-Matlab软件', '本书主要介绍了模式识别的基础知识、基本方法、程序实现和典型应用。全书共9章。第1章绪论,介绍模式识别的基本概念,基础知识;第2章介绍贝叶斯决策理论,第3章介绍概率密度函数的参数估计,第4章介绍非参数判别分类方法,第5章介绍聚类分析,第6章介绍特征提取与选择;第7章介绍模糊模式识别,第8章介绍神经网络在模式识别中的应用,', 2017, '杨杰', '978-7-121-32127-6', '', 'O235,TP183', '', '电子工业出版社', '2019-01-04 17:57:58', '', NULL, '1232890', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('398', '498', 'null', '模式识别及MATLAB实现学习与实验指导', '模式识别－计算机辅助计算－Matlab软件；人工智能－计算机辅助计算－Matlab软件', '本书是《模式识别及Matlab实现》主教材的配套实验与指导，根据主教材各章内容，相应给出了实验的具体步骤和程序代码，包括：贝叶斯决策，概率密度函数的参数估计，非参数判别分类方法，聚类分析，特征提取与选择，模糊模式识别，神经网络在模式识别中的应用，模式识别的工程应用等。', 2017, '', '978-7-121-32373-7', '', 'O235-39；TP183', '', '电子工业出版社', '2019-01-04 17:57:58', '', NULL, '1245686', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('399', '499', 'O235/X4712', '模式识别与智能计算的MATLAB实现', '模式识别-计算机辅助计算-Matlab软件,人工智能-计算机辅助计算-Matlab软件', '针对各学科数据信息的特点以及科学工作者对信息处理和数据挖掘技术的要求,本书既介绍了模式识别和智能计算的基础知识,又较为详细地介绍了现代模式识别和智能计算在科学研究中的应用方法和各算法的MATLAB源程序。本书可以帮助广大的科学工作者掌握模式识别和智能计算方法,并应用于实际的研究中,提高对海量数据信息的处理及挖掘能力,', 2017, '许国根,贾瑛,韩启龙', '978-7-5124-2400-5', '', 'O235,TP183', '', '北京航空航天大学出版社', '2019-01-04 17:57:58', '', NULL, '1223729', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
