INSERT INTO `sys_book` VALUES ('400', '500', 'F471.266/C332', '目标与关键成果法:盛行于硅谷创新公司的目标管理方法', '电子计算机工业-工业企业管理-目标管理-管理方法-美国', '本书将全面讲解其概念、意义、创建方法、实施方法和工具、技巧，执行过程中的难点和常见问题，激励模式，并通过完整的案例全方位展现实施流程。本书将帮助IT、互联网、科技等强调创新的公司，以及转型中的传统企业全面了解OKR，学会通过OKR保持员工与组织对齐。', 2017, '陈镭', '978-7-111-57349-4', '', 'F471.266', '', '机械工业出版社', '2019-01-04 17:57:58', '', NULL, '1225653', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('401', '501', 'F471.266/X1212', '苹果故事:乔布斯的简洁之道', '苹果公司-商业广告-市场营销学-经验,电子计算机工业-商业广告-市场营销学-经验-美国', '曾任NeXT和苹果公司创意总监、与史蒂夫·乔布斯共事多年的广告狂人肯·西格尔,回忆并记录了与乔布斯一起工作时发生的许多故事,从中我们看到乔布斯及其团队为了苹果产品做到简洁所做出的极致努力,无论是在产品、创意,还是营销中,简洁为苹果带来了独特的魅力和力量。阅读本书,就像在午夜时分与乔布斯通电话,了解简洁的精神实质,', 2017, '西格尔,王岑卉', '978-7-300-24283-5', '', 'F471.266', '', '中国人民大学出版社', '2019-01-04 17:57:58', '', NULL, '1217024', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('402', '502', 'R816.8-64/W199', '普通外科MRI解剖与诊断图谱', '外科-疾病-计算机X线扫描体层摄影-诊断-图谱', '本书是普通外科解剖图谱系列中的MRI分册,由多位相关领域的专家学者编写,他们都是国内各大医院第一线的MRI科研和工作者,有着丰富的临床工作经验和科学素养。本书章节按照普外科所属的分支进行系统编排,收录了各种常见疾病及部分罕见病,特别是搜集了编者们常年积累的大量珍贵病例图片,具有很好的参考价值,以飨读者。', 2017, '王劲,张水兴', '978-7-5359-6719-0', '', 'R816.8-64', '', '广东科技出版社', '2019-01-04 17:57:58', '', NULL, '1223749', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
