INSERT INTO `sys_book` VALUES ('414', '514', 'TP360.21/L798', '嵌入式计算机系统设计', '微型计算机-系统设计-高等学校-教材', '本书首先对嵌入式计算机软硬件开发模型进行了整体描述，然后自底向上地讲解了嵌入式计算机系统的体系结构、初始化和启动式型、开发环境、操作系统内核、文件系统、驱动程序及可视化开发模型，每个部分都配有实例，实例采用主流的嵌入式Linux和ARM9处理器进行说明。', 2017, '吕为工,张策', '978-7-5603-6133-8', '', 'TP360.21-43', '', '哈尔滨工业大学出版社', '2019-01-04 17:57:59', '', NULL, '1202598', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('415', '515', 'null', '嵌入式系统:硬件、软件及软硬件协同', '微型计算机-系统设计', '本书是一本系统讲解嵌入式系统架构的书，讲述了板级和FPGA级两种典型架构。全书分为三部分。第壹部分主要介绍了嵌入式系统；第二部分介绍嵌入式系统的硬件开发；第三部分介绍嵌入式系统的软件开发；第四部分介绍软硬件协同开发。', 2018, '诺尔加德,马志欣,苏锐丹,付少锋', '978-7-111-58887-0', '', 'TP360.21', '', '机械工业出版社', '2019-01-04 17:57:59', '', NULL, '1259573', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('416', '516', 'TP332/K1243', '嵌入式系统:体系结构、编程与设计', '微处理器-系统结构,微型计算机-系统设计-教材', '全书分为4大部分,第1部分介绍嵌入式系统设计和开发的整个过程以及涉及的相关技术,第2部分介绍8051、ARM微控制器、实时交互、输入与输出等,第3部分介绍架构技术与内存管理,第4部分介绍IO设备、通信总线以及分布式系统架构等。', 2017, '卡迈勒,郭俊凤', '978-7-302-46880-6', '', 'TP332,TP360.21', '', '清华大学出版社', '2019-01-04 17:57:59', '', NULL, '1206805', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('417', '517', 'null', '嵌入式系统案例设计教程', '微型计算机-系统设计-教材', '本书基于多核心嵌入式平台及单片机嵌入式平台这两个实际工程案例,介绍了嵌入式系统开发的完整流程,同时介绍了通过系统仿真提高设计效率、减少设计损耗的方法。本书涉及处理器有LPC2136、MSP430、8051和AVR,内容全面,本书还介绍了二十余种典型模块的设计方法。', 2018, '赖晓晨', '978-7-302-48204-8', '', 'TP360.21', '', '清华大学出版社', '2019-01-04 17:57:59', '', NULL, '1251649', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
