INSERT INTO `sys_book` VALUES ('436', '536', 'null', '全球视野中国实践:首届中外合作互联网治理论坛文集', '计算机网络管理-国际合作-文集', '2016年10月22日，以“全球视野，中国实践”为主题的“首届中外合作互联网治理论坛”在北京举办。本次论坛由中国社会科学院新闻与传播研究所和北京师范大学新闻传播学院主办，加拿大多伦多大学麦克卢汉中心和美国宾夕法尼亚大学互联网政策观察室协办。本文集选取了部分论坛发言和论文，从实践与探索、规制与模式、伦理与隐私、调查与数据、文', 2017, '唐绪军', '978-7-5203-0679-9', '', 'TP393.07-53', '', '中国社会科学出版社', '2019-01-04 17:57:59', '', NULL, '1255073', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('437', '537', 'null', '全球网络空间治理体系与中国方案', '计算机网络管理-研究-世界', '本书提出了网络空间内涵、全球网络空间治理的框架体系,主要包括治理目标、治理原则、治理主要内容、行为主体的权利和义务等。本书分析了全球网络空间治理的新趋势,提出了中国推动建立全球网络空间治理体系的政策建议。本书创新之处,主要包括:一是剖析了网络空间治理的理论基础,', 2017, '张影强', '978-7-5136-4805-9', '', 'TP393.07', '', '中国经济出版社', '2019-01-04 17:57:59', '', NULL, '1251651', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('438', '538', 'TP301.6/S828a', '群体智能优化算法及其应用', '计算机算法-最优化算法', '本书面向智能信息处理研究的前沿领域，针对群体智能优化算法及其应用中的关键问题，系统的讨论了新型群体智能优化算法以及群体智能优化算法在三维模型处理和可信软件测试中应用，比较全面的反映了国内外在文物三维模型智能处理和基于搜索的可信软件测试的最新研究进展。', 2017, '孙家泽,王署燕', '978-7-03-052898-8', '', 'TP301.6', '', '科学出版社', '2019-01-04 17:57:59', '', NULL, '1215266', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
