INSERT INTO `sys_book` VALUES ('456', '556', 'null', '身边的数学辅导员:用GeoGebra解决函数与方程问题', '数学教学－计算机辅助教学－应用软件', '古老的平面几何闪烁着人类智慧的光辉，许多经典内容成为千年传诵的传奇。免费数学教育软件GeoGebra，顾名思义地将几何（Geometry）与代数（Algebra）融为一体，它的价值不仅在于重温解析法的思想，更为重要地为大众理解数学、掌握数学，进行数学实验，领悟平面几何开辟了一条全新途径。本书为平面几何的初学者准备了许多浅显而基础性的几何内容，', 2017, '', '978-7-04-048521-9', '', 'O1-39', '', '高等教育出版社', '2019-01-04 17:58:00', '', NULL, '1245966', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
