INSERT INTO `sys_book` VALUES ('505', '605', 'null', '《资本论》与中国特色社会主义政治经济学', '资本论,中国特色社会主义-马克思著作研究,社会主义政治经济学-文集,文集', '本书以新的历史条件的视角，对《资本论》的基本原理和方法，进行了进一步的研究，对社会主义本质、社会主义初级阶段所有制结构、社会主义收入分配、社会主义公有制与市场经济的有机结合、政府和市场的关系、经济体制改革、经济发展新常态、新发展理念、供给侧结构性改革等问题进行了深入的探索。', 2017, '李建平,黄茂兴,黄瑾', '978-7-211-07799-1', '', 'A811.23-53|F120.2-53', '', '福建人民出版社', '2019-01-04 17:58:01', '', NULL, '1257643', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
