INSERT INTO `sys_book` VALUES ('507', '607', 'A811.23/Z773in', '《资本论》哲学的新解读', '马克思主义政治经济学-马克思著作研究', '《资本论》哲学研究是当前马克思哲学研究中最困难但又最有价值的领域之一。本书对《资本论》哲学的解释性研究，揭示出《资本论》哲学正是马克思哲学的实践形态，即它是作为实践的哲学、以实践的形态存在着的哲学或“实践”着的哲学。在此基础上，本书对《资本论》哲学的规范性研究，展示出《资本论》众多政治经济学议题所内在贯穿着的具�', 2016, '鲍金', '978-7-300-23103-7', '', 'A811.23', '', '中国人民大学出版社', '2019-01-04 17:58:01', '', NULL, '1153675', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('508', '608', 'F131.34/L334', '安倍经济学与中日经济关系', '经济学-研究-日本-现代,中日关系-经济贸易关系-研究', '本书以专题的形式，主要论述安倍经济学的实施及其前景对日本经济、中国经济发展尤其是中日经济关系的影响。在现阶段中日两国政治关系高度紧张、但经济交流依然重要的背景下，相关学者的分析及其结论不仅对理论界、对相关实务界人士也有很多参考、启发性。鉴于中国经济发展道路与日本经济发展的相似性，本书还侧重于分析、探讨了安倍经济�', 2016, '李晓', '978-7-01-016930-9', '', 'F131.34,F125.531.3', '', '人民出版社', '2019-01-04 17:58:02', '', NULL, '1174618', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
