INSERT INTO `sys_book` VALUES ('510', '610', 'X915.4/T343a2', '安全经济学', '安全经济学-高等学校-教材', '本书以安全经济规律为核心，系统、全面地介绍了安全经济学的基本理论和方法，重视基本概念、基本原理、基本方法的阐述，强调内容的系统性、科学性、新颖性。全书共11章，分别介绍了经济学基础及安全经济学的基本理论、事故损失计量、安全成本效益分析、安全投资分析、安全博弈分析、企业安全经济管理、安全会计分析、安全经济统计、安全经', 2017, '田水承,柴建设', '978-7-5646-3450-6', '', 'X915.4', '', '中国矿业大学出版社', '2019-01-04 17:58:02', '', NULL, '1214106', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('511', '611', 'X915.4/L8683', '安全经济学', '安全经济学', '《安全经济学》（第三版）是《安全健康新知丛书》（第三版）的一个分册。安全经济学是安全科学学科体系中重要且具有现实价值的子学科。本书基于多项国家级研究项目成果和作者多年的研究积累，给读者展现了安全经济学世界--一个非常规和具有负效益经济学特点的交叉科学世界。《安全经济学》（第三版）系统介绍了安全经济学原理、安全经济�', 2017, '罗云', '978-7-122-28371-9', '', 'X915.4', '', '化学工业出版社', '2019-01-04 17:58:02', '', NULL, '1206378', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
INSERT INTO `sys_book` VALUES ('512', '612', 'F091.343/T141', '奥地利学派经济学入门&米塞斯思想精要', '奥地利学派-经济学-研究,经济学家-经济思想-思想评论-奥地利-现代', '本书共分为八章,其主要内容包括:社会协作与资源配置;经济计算;主观价值论;市场与市场价格;均匀轮转经济中的生产;从均匀轮转经济到真实世界;通货膨胀与商业周期。', 2017, '泰勒,罗斯巴德,杨震', '978-7-5642-2770-8', '', 'F091.343,F095.21', '', '上海财经大学出版社', '2019-01-04 17:58:02', '', NULL, '1224944', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
